class Solution:    def findPeakElementdriver(self, nums,l,h):        mid = (l + h)//2        print(l,h,mid)                        if ((mid == 0 or nums[mid-1] < nums[mid]) and (mid == len(nums)-1 or nums[mid] > nums[mid+1])):            return mid;                 elif(nums[mid-1] > nums[mid] and mid > 0):            return self.findPeakElementdriver(nums,l,mid-1)                                           else:            return self.findPeakElementdriver(nums,mid+1,h)             def findPeakElement(self, nums):        l = 0;        h = len(nums) - 1        return self.findPeakElementdriver(nums,l,h)                                                               s = Solution();nums = [1,2,3]ans = s.findPeakElement(nums);print("Answer:",ans)